mapper.select:
SELECT * FROM MEMBER
WHERE ID = #{id}

	AND PASSWORD = #{password}

mapper.update:
UPDATE MEMBER
 SET PASSWORD = #{password}
 SET NAME = #{name}
 SET TEL = #{tel}
 SET ADDRESS = #{address}
 SET AUTHOR = #{author}
 SET IMGFILE = #{imgFile}
 SET PIMGFILE = #{pimgFile}
WHERE ID = #{id}

mapper.select:
SELECT (CASE WHEN COUNT(*) = 1 THEN 1 ELSE 0 END) AS RESULT
FROM MEMBER
WHERE ID = #{id}
